css:
#edit-controls,#zoom-controls{
    height:30px;
}

toolbarspring,toolbarspacer{
    display:none;
}

#PanelUI-contents *{
    -moz-box-orient: horizontal!important;
    padding:0!important;
    margin:0!important;text-align:left;
}
#PanelUI-contents > toolbarbutton,
#PanelUI-contents > toolbaritem > toolbarbutton{
    height:40px;
    width:50%;
}
/*#PanelUI-contents image:not([class="dropmarker-icon"]){*/
#PanelUI-contents .toolbarbutton-icon:not([label="縮小"]):not([label="拡大"]){
    max-height:20px!important;
    min-height:20px!important;
    max-width:20px!important;
    min-width:20px!important;
}


/******************* カスタマイズ画面 **************************/
/*左側*/
#customization-palette toolbarpaletteitem{
    margin:0!important;
}
#customization-palette .toolbarbutton-icon{
    max-height:20px!important;
    min-height:20px!important;
    max-width:20px!important;
    min-width:20px!important;
}

/* 右側 */
#PanelUI-contents toolbarpaletteitem[id="wrapper-customizableui-special-spring29"],
#PanelUI-contents toolbarpaletteitem[id="wrapper-customizableui-special-spacer31"]{
    display:none;
}

#PanelUI-contents toolbarpaletteitem:not([id="wrapper-edit-controls"]):not([id="wrapper-zoom-controls"]){
    height:40px;
    width:50%;
}
