css:
@-moz-document domain("edux.fit.cvut.cz"), domain("e-learning.fit.cvut.cz") {
body {
  font-size:1em;
}
div#termsForm p.info {
  font-size:1em !important;
}
.noteclassic, .noteimportant, .notewarning, .notetip,
div.dokuwiki div.page_with_sidebar {
  text-align:left;
}
img {
  max-width:100%;
  height:auto;  
}
div.dokuwiki img.media,
div.dokuwiki div.page,
div.dokuwiki div.preview {
  margin:0;
}


@media all and (max-width: 40em) {

div.dokuwiki div.page_with_sidebar {
  text-align:left;
  padding-left:0;
  width:auto;
  float:none;
}
div.picker {
  width:auto;
}
#sidebar {
  display:none;
}

}
}
