css:
@-moz-document url-prefix("http://www.minecraftforum.net/forums/") {
.p-comment-container{
    border: 1px solid #b4c0ca !important;
}
.p-comment-sig {
    max-height: 38px !important;
    width: 81% !important;
    padding: 0px !important;
    overflow: hidden !important;
    float: left;
}
.p-comment-sig:hover {
    max-height: none !important;
    max-width: none !important;
    width: 100% !important
}
.p-comment-sig:hover + .p-comment-footer {
    display: none !important;
}
.p-comment-user {
    max-height: 95px !important;
    overflow: hidden !important;
}
.p-comment-user:hover {
    max-height: none !important;
}
.p-comment-user .p-comment-avatar {
    transform: scale(0.3, 0.3);
    margin-top: -5px !important;
    height: 35px !important;
}
.p-comment-user:hover .p-comment-avatar {
    transform: scale(1, 1);
    margin-top: 5px !important;
    height: 140px !important;
}
.p-comment-stats {
    margin-top: 23px !important
}
.p-comment-statitem {
    margin-bottom: 0px !important
}
.p-comment-content {
    min-height: 40px !important;
}
.p-comment-postdate {
    float: right !important;
}
.p-comment-header {
    width: auto !important;
    position: absolute !important;
    right: 0px !important;
    opacity: 0.1 !important;
    z-index: 999 !important
}
.p-comment-header:hover {
    opacity: 1 !important;
}
.p-comment-index {
    padding-left: 10px !important;
}
.p-comment-footer {
    position: absolute !important;
    width: 19% !important;
    right: -1px !important;
    bottom: 0px !important;
    opacity: 0.1 !important;
}
.p-comment-footer:hover {
    opacity: 1 !important
}
.forum-post-rating {
    float: right !important;
    padding-left: 10px !important;
}
.p-comment-actions li.user-action span.label {
    position: absolute !important;
    display: none !important;
    top: -15px !important;
    z-index: 1000 !important
}
.p-comment-actions li.user-action:hover span.label {
    display: inline-block !important;
    white-space: nowrap !important;
    background-color: #eee !important;
}
}
