@-moz-document domain("foodnetwork.com") {

@media print {a[href]:after {content: none !important;}
.lead-overview figure img{display:none !important;}
.lead-overview figure figcaption{display:none !important;}
.lead-overview .cooking-times{display:none !important;}
.lead-overview .difficulty{display:none !important;}
.lead-overview .avatar h6{display:none !important;}
.lead-overview hr{display:none !important;}
.lead-overview .avatar .copyright{display:none !important;}
.printable .lead-overview .avatar{display:none !important;}
}
}
