css:
@-moz-document url("http://tinychat.com/spyc0"), url("https://tinychat.com/spyc0"), url("http://tinychat.com/ibotiscool"), url("https://tinychat.com/ibotiscool"), regexp("[*.]tinychat.com/spyc0"), regexp("[*.]tinychat.com/ibotiscool") {
	@-moz-document domain("tinychat.com") {
	#left_block {width: 100% !important; height: 100% !important;}
	#chat {height: 100% !important;}
	#room {padding: 0px; width: 100%; height: 100%; }
		 #tinychat,
		 #tinychat_sub,
		 #wrapper,
		 #page,
		 #container,
		 #left_block
		{
			width: 100% !important;
			height: 100% !important;
		}
		 #chat
		{
			width: 100% !important;
			height: 100% !important;
			background: rgba(0, 0, 0, 0);
		}
		 #preload
		{
			position: fixed;
			height: 100%;
			width: 100%;
		}
		 #header
		{
			position: fixed;
			right: 0px;
			height: 0px;
			width: 10%;
			z-index: 50;
			background: rgba(0, 0, 0, 0);
			-webkit-box-shadow: 0 0px 0px rgba(0, 0, 0, 0);
		}
		 #room_header
		{
			position: fixed;
			right: 0px;
			top: 153px;
			width: 10%;
			padding: 0px;
			height: 100%;
			background: rgba(0, 0, 0, 0);
		}
		 #room_info
		{
			top: 0px;
		}
		 #room-avatar
		{
			top: 0px;
		}
	}
}
