css:
@-moz-document domain("yahoo.co.jp") {
body,a {
  font-family: Verdana, 'ヒラギノ角ゴ Pro W3', 'Hiragino Kaku Gothic Pro', 'メイリオ', Meiryo, sans-serif;
}

a,
.subRanking li .ttl{
  text-decoration: none!important;
}

a:hover {
  text-decoration: underline!important;
}

#searchbox,
#topicsboxbd .topicsdetail{
  border-width:0!important;
  border-radius: 7px;
}
#srchbtn {
  border-radius: 7px;
}


#pbproperty,
#pbtoday,
#pbalert,
#tct,
#commerce,
#rightbox,
#personalbox{
  border-width:0!important;
}

.bx {
  border-width:1px 0 0 0!important;
}

#navi .bx {
  background-color: #fff;
}

#contentbox ul li {
  padding: 5px 0 0px;
}

#companybox div {
  margin-top:5px;
}
}
