css:
@-moz-document domain("gigazine.net") {
/* ----- ページ全体 -----*/

body,
#outer,
#inner,
.sbn,
#maincol .content,
#pagewidth,
#compagewidth, #schpagewidth,
h3.date, .preface, .article, .posted, big, b, blockquote, #maincol .content ul li, #inq dt, #inq dd, ol li {
	background: #fff!important;
}

body, #outer, #inner, .sbn, #maincol .content,
h1, h2, h3, a, p, span, blockquote, th, td, #maincol .content ul li, #inq dt, #inq dd, ol li, .border,
.date a, h3.date, #paginate, .paginate, .pagecount, .calendarBlank, .calendarMonthLinks, .calendarToday, .calendarCell {
    font-family: Verdana, 'ヒラギノ角ゴ Pro W3','Hiragino Kaku Gothic Pro','メイリオ', Meiryo, sans-serif;
}

h1, h2, h3 {
    font-family: Verdana, 'ヒラギノ角ゴ Pro W6','Hiragino Kaku Gothic Pro','メイリオ', Meiryo, sans-serif;
    font-weight: 700;
}

#pagewidth,
#compagewidth,
#schpagewidth {
	margin: 0 30px 0 30px;
    border-right: 0px;
}

a:link {
    color:#4b8be3;
}
a:visited {
    color:#9a9a9a;
}
a:hover,a:active {
    background-color:#fff;
    color:#4b8be3;
    text-shadow: 0 0 5px #b4d4ff;
}

/* ----- ヘッダ -----*/

#header {
    padding-bottom: 5px;
    border-bottom: 1px solid #999;
}

#logo {
	max-height:80px;
}
#logo img,
#logo #headbn {
	max-height: 80px;
	margin-top: 15px;
    width: auto;
}
div#logo:before {
    content: "Gigazine";
    color:#999;
    font-family: 'Century Gothic', Helvetica, Verdana, sans-serif;
    font-size: 64px;
	font-weight: 400;
}
#logo img {
    visibility: hidden;
}

#headerbtm {
	padding-bottom: 5px;
}

/* ----- 左カラム -----*/

#leftcol {
    margin-top: 10px;
}

.sidetitle {
	font-size: 100%;
	font-weight: 400;
	color: #fff;
	background-color: #666;
	border-radius: 5px;
}
.sidetitle span {
    padding: 5px 5px 3px 8px;
}

#leftcol ul li, #rightcol ul li {
    font-size: 80%;
    margin: 1em 0 1em 0px;
    padding-bottom: 1em;
    line-height: 1.5em;
    border-bottom: 1px solid #ccc;
}
#newslist ul {
    list-style:none;
}

.left{
    font-size: 80%;
    color: #666;
}
.left h3{
    font-size: 125%;
}

/* ----- メインカラム -----*/
#maincol {
    padding: 0 30px 0 30px;
    min-width: 240px;
    width: auto;
}

#grid-content .box{
    height: auto;
    overflow: visible;
    margin-right: 20px;
}
#grid-content .box h2.title,
#content h1,
.title,
a.mytitle:link, a.mytitle:visited, a.mytitle:active {
    height: auto;
    padding: 10px 0 20px 0;
    color: #666;
    background: #fff;
    line-height: 1.4em;
}

.date a, h3.date, #paginate, .paginate, .pagecount, .calendarBlank, .calendarMonthLinks, .calendarToday, .calendarCell {
    font-weight: 400;
}

.date {
    color: #6e59af;
    margin: 8px 0 8px 0;
    font-weight: bold;
}

.catab {
    display: block;
    padding: 2px 5px 2px 5px;
    font-size: 90%;
    border: 0px;
    border-radius: 5px;
}

/* ----- メインカラム（記事ページ） -----*/

h3.date {
    color: #999;
    border-bottom: 1px solid #999;
    padding-bottom: 5px;
}
.preface, .article {
    line-height: 1.7em;
}
.article {
    margin-top: 10px;
}
.posted {
    display: block;
    padding: 2px 10px 2px 10px;
    background: #eee!important;
    border-radius: 5px;
}

.content img {
    border-radius: 7px;
}

#topsy-tweet-search-results {
    margin-top: 10px;
}
#topsy-tweet-search-results h4 {
    margin-bottom: 10px;
}
.topsy-tweet-search-result {
    padding: 5px 10px 5px 10px;
    border-top: 1px solid #fff;
    border-bottom: 1px solid #fff;
    border-radius: 7px;
    margin-bottom: 5px;
}
#nextpage, #topsy-show-more-tweets {
    padding: 5px 10px 5px 10px;
}

/* ----- メインカラム（入力フォーム） -----*/

#inq,
#inq dl,
#inq dl dt,
#inq dl dd {
    width: 100%;
    min-width: 483px;
}

#inq .qmail, #inq .q1, #inq .q2 {
    width: 483px;
}

#inq .description,
#inq .comment {
    display: block;
    font-size: 95%;
    color: #666;
    background: #f9f9e5;
    padding: 5px 10px 5px 10px;
    margin: 10px 0 10px 0;
    border-radius: 5px;
}

li {
    line-height: 1.5em;
    font-size: 95%;
}

/* ----- 右カラム -----*/

#rightcol {
    width: 200px;
    margin-right: -200px;
    margin-top: 10px;
    text-align: center;
}

dl.acdn, dl.schacdn {
    margin: 10px 0 10px 0;
}

dl.schacdn dt, dl.acdn dt,
dl.schacdn dt.active, dl.acdn dt.active {
    font-size: 100%;
    font-weight: 400;
	color: #fff;
	background-color: #666;
	border-radius: 5px;
    border-bottom: 0px;
    margin-bottom: 10px;
}

/* ----- フッター -----*/

#footer {
    border-left: 1px solid #000;
    border-right: 1px solid #000;
}
}
