/*
	Author:		parsifal
	Style name:	Insomnia.gr - Floating scroll to top button
	Version:	0.1
	Date:		20140507
*/

@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("insomnia.gr") {
	#backtotop {
		position: fixed;
		left: auto;
		right: 48px;
		bottom: 48px;
		width: 36px;
		height: 36px;
		border-radius: 18px;
		line-height: 0;
	}

	#backtotop img {
		position: relative;
		top: 4px;
		left: 1px;
		width: 24px;
		height: 24px;
	}
}
