css:
@-moz-document url-prefix("https://www.humblebundle.com/store") {
body {
  	background: #363636 !important;
}

h2.under-logo {
	color: white !important;   
}

.product-description .editable-html {
	color: white !important;   
}

.storefront-background {
	opacity: 0 !important;   
}

.storefront-list-product h2 {
	color: white !important;   
}

.storefront-list-product .platforms-and-buy-button .platforms li > i {
	color: white !important;
    opacity: 0.8 !important;
}

.storefront-list-product .scrollable-wrapper {
	border: 1px solid rgba(188, 191, 198, 0.8) !important;   
}

.storefront-filter-controls h2 {
	color: rgba(188, 191, 198, 1) !important;
}
}
