css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("google.com"), domain("google.ru"), domain("google.de"), domain("google.com.ua"), domain("google.fr"), domain("google.co.uk"), domain("google.kz"), domain("google.lt"), domain("google.lv") {
    
    /* Блоки поисковой выдачи */
    h3 a, #res #imagebox_bigimages a:not(#irl_r_a), #res #newsbox a {
        font-size: 16px !important;
        text-decoration: underline !important;
    }
    
    
    /* Текст поисковой выдачи*/
    #res .st {
       color:#444 !important;    
    }
    
    
    /* блок рекламы */
    ol>li.ads-ad a {
        background: transparent !important;
    }
    ol>li.ads-ad {
       padding-left: 8px !important;
       padding-right: 8px !important;
       background: #ffffdd !important;
       color:#373737 !important;
    }
    
    
    /* Блок рекламы товаров */
    .commercial-unit h3 {
       font-size: 16px !important;
    }
    .commercial-unit  {
        background: #ffffdd !important;
    }
    .commercial-unit * {
        background: transparent !important;
    }
    
    
    /* блок "Вместе с * часто ищут" */
    #brs a {
       text-decoration: underline !important;
    }
    #brs .med {
       font-size: 16px !important;
       color: #000 !important;
    }
    
    /* блок "Похожие службы" */
    table .ts a {
        font-size: 14px !important;
    }
    
    /* Блок "Возможно, вы имели в виду *" */
    .spell {
       font-size: 16px !important;
    }
   
    
       
}
