css:
@-moz-document domain('docs.google.com') {
	#docs-editor {
background: url(http://i.imgur.com/ZufpuAT.jpg) !important;
}

.docos-layout-anchored .docos-anchoreddocoview {
border: 5px solid #5C1981;
}
.docos-anchoreddocoview {
background-color: rgba(164, 0, 255, 0.18);
}

.docos-anchoreddocoview:hover, .docos-docoview-passive.docos-anchoreddocoview {
background-color: rgb(168, 129, 168);
border-color: #5C1981;
color: black;
}

.docos-docoview-active.docos-anchoreddocoview, .docos-docoview-active.docos-anchoreddocoview:hover {
background: #F1BA86!important;
border-color: #AF7946!important;
}
.docos-layout-vertical .docos-anchoreddocoview-arrow-inner {
border-right: 14px solid #F1BA86!important;
}

.docos-docoview-active.docos-anchoreddocoview .docos-anchoreddocoview-input-pane {
border-top: 1px solid #AF7946!important;
}

#docs-chrome {background: url(http://i.imgur.com/dawHI48.jpg);}

.docs-vis-ref-chrome #docs-toolbar-wrapper{
border-color: #9E832C;
background: url(http://i.imgur.com/WdjPwZP.jpg);
}

.docs-vis-ref-chrome .docs-slidingdialog, .docs-vis-ref-editor-container .docs-slidingdialog {
background: url(http://i.imgur.com/WdjPwZP.jpg);
border-color: black;
}

.docs-title-inner {font-family: Georgia !important;}
.docs-menubar {height:28px !important;}

body {font: 14px Georgia;}
.docs-menubar {font: 12px Georgia;}
.docos {font-family: Georgia;}
}
