css:
@-moz-document domain("veetle.com") {

html, body, dl, dt, dd, ul, ol, h1, h2, h3, h4, h5, h6, pre, code, form, fieldset, legend, input, textarea, blockquote, th, td, #header, select, .photo, #searchNoResults {
    color: #999 !important;
    background-color: #000000;
    text-decoration:none;
    text-shadow: none;
  }
#schedule, #schedule li.selected, #footer *, #header, #listingnav  {background: none !important;}
.stream, p {color: #999 !important;}
.stream > a > span, #account * {background-color: #000;}
#panelChannels ol li.selected, #listingNav, #listingNav > li > ol, #panelChannels ol li:hover {
    border: 0.1em solid #222;
    background: none repeat scroll 0% 0% #101010;
    box-shadow: none;
    color: #CCC;
    }
#listingNav > li {
    border-left: 0.1em solid #000;
    border-right: 0.1em solid #000;
}
#panelChannels ol li, #navigation li a, #account li > a span, .tabs li, .tabs li.selected, #channelDescriptionContainer, input[disabled], select[disabled], textarea[disabled], input[readonly], select[readonly], textarea[readonly], .button, #listingNav > li a > span   {
    border: none;
    background: none;
    text-shadow: none;
}
#listingNav > li a > span {color:#CCC;}
input[type="text"] {
    box-shadow: 0px 1px 1px rgba(0, 0, 0, 0.075) inset;
    background: none;
}
}
