css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("izhevsk.ru") {
    #dialogs, textarea {font-size: 16px !important;}
    #bottom-link {top: 200px !important;}      /* стрелка вниз */
    table tr > td > font {font-size: 16px !important;}
    .size1 {font-size: 8pt !important;}
    td.altcolor1 br {display: none !important;}
    table.tablestrip {display: none !important;}   /* похожие темы */
    .yashare-auto-init {display: none !important;}   /* соц. сети */
    
    .rightadv,
    .advert,
    #banner50825big,
    #banner50825small
    {
      display: none !important;
    }   /* реклама */
    
    body > center:nth-child(3) > table:nth-child(5) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1) > form:nth-child(2) > center > table:nth-child(1) > tbody:nth-child(1) > tr > td:nth-child(3) {
      width: 600px !important;
      padding: 0.15em !important;
    } /* название темы */
    
    body > center:nth-child(3) > table:nth-child(5) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1) > form:nth-child(2) > center > table:nth-child(1) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(4) {
      font-size: 14px !important;
      white-space: pre-line !important;
    }   /* автор темы */
    
    body > center:nth-child(3) > table:nth-child(5) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1) > form:nth-child(2) > center > table:nth-child(1) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(5) {
     font-size: 14px !important;
     width: 100px !important;      
    } /* последний комментатор темы */
    
    body > center:nth-child(3) > table:nth-child(5) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1) > form:nth-child(2) > center > table:nth-child(1) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(6) {
     font-size: 14px !important;
     width: 175px !important;
    } /* дата создания и последнего комментария */
    
}
