css:
@-moz-document domain(bankofinternet.com) {

  /* Global */
  a:hover {
    text-decoration: underline !important;
  }

  table.NT {
    margin-top: 10px !important;
  }
  table.NT tr.AccentTR, table.NT th, table.NT td {
    background-color: transparent !important;
  }
  table.NT th.Header {
    background-color: #ccc !important;
    color: #000 !important;
    padding: 4px !important;
  }

  /* Launch bar */
  #launchBar_container, #launchBar {
    background-color: transparent !important;
  }
  #launchBar li {
    padding: 0 !important;
  }
  #launchBar li a {
    font-weight: normal !important;
    padding: 0 5px !important;
    color: #999 !important;
    font-size: 11px !important;
  }

  /* bank info */
  #bankaddress {
    display: none !important;
  }

  /* Top level nav */
  #tabBar_container, #tabBar {
    background-color: transparent !important;
  }

  #tabBar {
    padding: 0 !important;
    border-bottom: 1px solid #036 !important;
  }

  #tabBar li {
    margin-right: 1px !important;
  }

  #tabBar li a {
    padding: 8px !important;
    background-color: #ccc !important;
    line-height: 30px !important;
    color: #333 !important;
  }
  #tabBar li a:hover {
    text-decoration: underline !important;
  }

  #tabBar li.selected a {
    background-color: #036 !important;
    color: #fff !important;
  }

  /* Sub nav */
  #menuBar {
    padding: 3px !important;
    background-color: #036 !important;
  }
  
  #menuBar li {
    padding: 0 !important;
    border: 0 !important;
    background-color: transparent !important;
  }

  #menuBar li a {
    color: #fff !important;
    font-weight: normal !important;
    font-size: 11px !important;
    padding: 0 5px !important;
  }
}
