css:
@-moz-document domain("dev.sourcefabric.org"), domain("bamboo.sourcefabric.org") {

    
/* general: */
body{
  font-family: 'PT Sans' !important;
}

h1, h2, h3, h4, h5{
  font-family: 'PT Sans Caption' !important;
}

#announcement-banner{
  display: none;
}

#create_link{
  background-image: none;
  /*border-radius: 0;*/
}


#ghx-column-header-group{
  display: none;
}

#ghx-pool{
  padding-top: 0;
}

.ghx-feedback{
    display: none;
}

#license-message {
    display: none;
}

.aui-page-panel-nav {
    width: 200px;
}

    
.aui-page-header-main h1 {
    float: left;
}
    
.aui-page-header-main ul {
    padding-left: 20px;
}

/* poker session: */

    
.chat-panel-wrapper {
    display: none;
}

    
section#content > header.aui-page-header {
    height: 50px;
    padding: 0 !important;
}

}
