css:
@-Juve-document domain("kaskus.co.id") {
    /* nav */
    body.q ul.nav-header > li#kk-forum.current a {background: #3399ff;}
    
    /* home page */
    #mainContent {background: #f5f5f5; border-radius:4px;}
    ul#sidebar-category > li {background: #fbc739;}
    .h-title {color:#fdbd11; border-bottom: #fbc739;}
    .hot-wrap .ht ul li a {color: #444444;}
    .hot-wrap .ht ul li {border-bottom: 1px solid #444444;}
    
    /* reply page*/
    .bannerhor {background: #f4f4f4;}
    body.forum .kasad-h {background-color: #3399ff;}
    body.q {background: #444444 url(http://cdn.kaskus.com/images/2014/05/20/4885975_20140520114820.jpg)}
    body.q header#site-header-q {background: #f0f0f0 url(http://cdn.kaskus.com/images/2014/05/21/4885975_20140521094106.png)}
    body.forum .hfeed {background: #dedddd;}
    body.forum .hfeed .entry {background: #dedddd;}
    .hfeed .author {background: #dedddd;}
    .hfeed .entry-head {background-color:#3399ff;}
    body.forum #forum-listing .button {background-color:#3399ff;}
    body.forum .hfeed:after {background-color:#dedddd;}
    
    /* pagination */
    body.forum.inner.q .pagination .active a {background:#3399ff;}
    
    /* single post */
    body {background: #67b0fa;}
    .hfeed:after {background-color: #f4f4f4;}
    
     /* footer page */
    footer {background: #0f0f0f;}
}
