css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("trello.com") {
    *{
       font-size: 8pt;
    }
    
    .list-link, .list-card-title, .phenom-desc, .card-detail-desc{
       line-height: 9pt;
    }
    
    body, html, div, p{
       font-family: Consolas, Arial, sans-serif;
    }

    .list-card-labels .card-label {
       height: 9px;
       margin-bottom: -6px;
       line-height: 8px;
       text-align: center;
       width: 50px;
       font-size: 10px;
    }
    
    .card-short-id {
       margin-right: 4px;
       padding: 0 2px;
       position: relative;
       border-radius: 2px;
       box-shadow: 1px 1px 1px #DDD;
       -webkit-box-shadow: 1px 1px 1px #DDD;
       color: #777;
       text-decoration: none;
       margin-top: 3px;
       font-size: 11px;
       line-height: 14px;
       background-color: #FFC;
       display: inline;
       border: 1px solid #CCC;
       float: left;
       height: 14px;
    }
}
