css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("keyboard-layout-editor.com") {
  @media print {
    #wrap > .navbar,
    #wrap > .body > .btn-group,
    #keyboard ~ *,
    #keyboard + *,
    #footer,
    #helpDialog {
      display: none;
    }
    #keyboard {
      border: none;
      background-color: inherit !important;
    }
  }
}
