css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("socialgamer.net") {


  
    body {
        color: #C2C2C2;
        background-color: #1A1A1A
    }
    .whitecard, .gamelist_lobby {
     background-image: linear-gradient(#5D5D5D, #4B4B4B 75%, #2D2D2D)
         }
    .scorecard:nth-child(2n) {
        background: none repeat scroll 0% 0% #1A1A1A
            }
    .scorecard {
        background: none repeat scroll 0% 0% #1A1A1A
            }
    .log {
        background-color: #1A1A1A;
        color: #CCC
    }
    .ui-tabs .ui-tabs-nav li a, .ui-tabs-collapsible .ui-tabs-nav li.ui-tabs-active a {
    background-color: #2A2A2A;
    color: #CCC
        }
    .ui-widget-content {
        background: url(http://i.imgur.com/SXZF2oP.png)
            }
    .ui-widget input, .ui-widget select, .ui-widget textarea, .ui-widget button {
        background-color: #1A1A1A;
        color: #CCC
            }
    .ui-widget-header {
        background: url(http://i.imgur.com/3nAaWvb.png)
            }
    .game_hand_filter {
        background-color: rgba(77,77,77, .5)
            }
    .game_hand_filter_text {
        background-color:rgba(44, 44, 44, 1)
            }
    .game_options {
        background: none repeat scroll 0% 0% #2A2A2A
            }
    a {
        color: rgb(70,100,255)
    }
    
}
