css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("popmundo.com") {

/* Colors red */
a[title$="500"], a[title$="1000"], a[title$="2000"], a[title$="5000"] {
font-weight:bold !important;
color:#f00 !important;
}

/* x500 fix */
a[title$="1500"], a[title$="2500"], a[title$="3500"], 
a[title$="4500"], a[title$="5500"], a[title$="6500"], 
a[title$="7500"], a[title$="8500"], a[title$="9500"] {
font-weight:normal !important;
color:#33647D !important;
}

/* 10'000 tickets */
a[title$="10000"]:after{content:" *sold out*"; color:red !important; }

}
