css:
@-moz-document url("http://iboodhunt.1dagskoopjes.nl/") {
body{
	background:#1e1e1e;
}

body a{
	color: #95c11f;
}

.chat-msg-container,.main-deal{
  	color:#000;
}

div.caption> div,.caption,.main-deal a:hover,body { 
  	color: #fff !important; 
}

#twitter-container,#radio-container,.bs-icon-header,#chat-container h2,.navbar,footer,.row-fluid .span4,.row-fluid .span6,#chat-btns,.bs-icon-header-big,.serverstatus-container,.countries,#alerts,#realtimealert,.alerts{
    display:none;
}

.main-deal{
	height:370px;
}

#chat-container{
  	width:100%;  
  	margin-top:160px;
}

#chat-message-input{
	width:80%;
}

.row-fluid .span8{
    width:auto;
}


#deal_template{
	display:block;
  margin:0 auto;
}


#chat-container #smilies{
	display:inline;
}

.dealtitle{
	max-width:600px;
}

#deal_template{
  position:relative;
  display:block;
  margin:0 auto;
  left:22%;
}

div.span12.main-deal > #deal > #deal_template > .span8 > div:nth-child(4)>div {
text-align: center !important;
 }

div.span12.priceCompare {
text-align: center; 
}


.crosshair-big{
	position:fixed;
	left:0px;
	top:0px;
	width:100%;
    z-index:9999;
}

#chat-container a{
	color:#013bec;
}

#chat-container p a{
	color:#b0ec01;
}

.main-deal{
	background:#000;
  	color:#fff;
}

#deal_template .span4{
  	display:block;
    width:300px;
}  

.btn-warning {
	background-color: #3aaa35;
	border-style:none;
  	margin-top:-15px;
}
.btn-primary{
  	background-color:#035f7e;
	border-style:none;
    margin-top:-15px;
}
.btn-primary:hover{
  	background-color:#0484b0;
}
.btn-warning:hover{
	background-color: #95c11f;
}
}
