@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix("http://www.youtube.com/watch?v="), url-prefix("https://www.youtube.com/watch?v="){
    #yt-masthead-container{
        display: none !important;
    }
    #watch7-content{
        position: fixed !important;
        top: 60% !important;
        left: 0px !important;
        width: 100% !important;
        height: 40% !important;
        overflow-y: scroll !important;
        z-index: 2 !important;
    }
    #watch7-sidebar{
        display: none !important;
    }
    #footer-container{
       display: none !important;
    }
    #player-api *{
        display:block !important;
        position: fixed !important;
        top: 0px !important;
        left: 0px !important;
        width: 100% !important;
        height: 60% !important;
    }
}
