css:
/* i really want this to be global */

iframe[id^="lpformframe"] {
    width:375px !important;
}

#LPPopup #superbox_savesitea #superbox_savesite_icon {
    -moz-box-sizing: border-box !important;
    box-sizing: border-box !important;
    background: url(data:image/png;base64,) no-repeat center !important;
    padding: 15px !important;
    opacity: 0.25 !important;
}



#LPPopup #main, #lptabpopupsave ,#lptabpopupsave p, #lptabpopupsave label, #doGenerateDiv, #lpgenerate, #generate .gendivtext, #generate #advancedoptions, #generate label, #generate a {
    background: none repeat scroll 0% 0% #232323 !important;
    color: #CCC !important;
}

#LPPopup #main td:hover, #LPPopup .tablerowfirefox:hover {
    background: none repeat scroll 0% 0% #333 !important;
    color: #CCC !important;
}

#LPPopup #superbox span {
    color: #CCC !important;
}

#LPPopup #superbox, #LPPopup #superbox a, #LPPopup #superbox img {
    background: none repeat scroll 0% 0% #1A1A1A !important;
}

#lptabpopupsave input, #lptabpopupsave #group_combo, #generate #password, #generate #password_combo, #generate select {
    background: none repeat scroll 0% 0% #1A1A1A !important;
    color: #AAA !important;
}

#lptabpopupsave #group_combo .item.focus, #generate #password_combo .item.focus {
    background: none repeat scroll 0% 0% #333 !important;
    color: #AAA !important;
}

#generate {width: 347px !important;}

#generate #password {
    width: 283px !important;
    padding-right: 5px !important;
}
