css:
@-moz-document domain("digbysblog.blogspot.com"){ 
 html {
     font: 10pt sans-serif !important;
  }
   body{
       background-color: lightyellow !important;   
   }
    
  .outerborder{
      border: none;
      text-align: left;
      font: 10pt sans-serif !important;
  }
    
  .contentcell {
    width:750px !important;
    background-color: white !important;
    border: solid black 1px !important;
    padding: 5px 0px 5px 20px !important;
    font: 10pt sans-serif !important;      
  }

 .contentcell blockquote blockquote{
        padding: 10px !important;
        background-color:#FCFCFC !important;
        border: dotted gainsboro 1px !important;
        /*width:90% !important;*/
        /*background-color: #EFEFF2 !important;*/
    }   
    
 blockquote blockquote span{
        background-color:white !important; 
 }

a:link, a:visited {
    color: red;
    text-decoration: none;    
}
    
a:hover, a:active {
    color: black;
    text-decoration: underline;
}    

.contentcell blockquote{
    padding-top:10px;
    border-top: 1px black solid;
}

   .contentcell blockquote b{
    font-size: larger !important;
}
}
