css:
@-moz-document domain("www.tumblr.com") {
*{
  text-shadow:none !important;
  color:grey !important;
}

.search_form_row {
background:white!important;
border-radius:5px;
border:1px solid #ffa8a8;
}
    
body {
background: url('http://static.tumblr.com/9a5e297559feb2ac735c2fd558ac2c06/bj5ceom/bcGn5fsey/tumblr_static_d83kodkol3k8co0k4go84owkc.png') no-repeat left bottom fixed, url('http://static.tumblr.com/a87117b3462fe772699fb021ad8f0e1b/bu5y7uo/Gtjn5bmol/tumblr_static_80f0k6npz8080oskcgwwks00k.png') repeat fixed;
}

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
background: none;

}


.post {
	background:white;
border:1px solid #ffa8a8;
	border-radius: 5px;

}

.post_content{
	background: transparent !important;
}






#right_column{
background-color:white!important;
border:1px solid #ffa8a8;
border-radius: 5px;
}


.selection_nipple { display:none !important }

div.selection_nipple {
display:none !important;
}

.nipple { display:none !important }

div.nipple {
display:none !important;
}

span.tab_notice_nipple {
  display:none!important;
}

div.tab_notice {
background:none !important;
}

.post_avatar_image {
border: 1px solid rgba(0,0,0,0);
}
#new_post_notice_container.tab_notice {
color:white !important;
  background-color: #ffa8a8 !important;
    border: 1px solid rgba(0,0,0,0);
border-radius:5px;}

}
