css:
@-moz-document url-prefix(https://bitcoinwisdom.com/) {
	#header .logo{
		display: none;
	}
	#header .navbar li{
		font-size: 10px;
		line-height: 10px;
		margin-right: 5px;
	}
	#header .navbar li span{
		margin-left: 0px;
	}
	#leftbar_outer{
			display: none !important;
	}
	#control{
		font-size: 10px;
		line-height: 10px;
	}
	#periods .li:first-child{
		display: none;
	}

	#sidebar #market{
		font-size: 10px;
		line-height: 10px;
		height: 10px;
	}
	#header .difficulty{
		display: none;
	}
	#orderbook .table .row{
		height: 10px;
		line-height: 10px;
		font-size: 9px;
	}
	#trades .row{
		height: 10px;
		line-height: 10px;
		font-size: 10px;
	}
	#footer{
		display: none;
	}
}
