css:
@-moz-document domain("dongxi.douban.com") {
.home-recommend-col2 {
  display: none;
}

.home-recommend-col1, .dx-nav {
  width: 950px !important;
}

.dx-nav .bd {
  overflow: hidden;
}
  
.dx-nav .bd > ul {
  width: 665px;
  float: left;
}
  
.dx-nav .bd > ul > li {
  display: inline-block;
  min-width: 5em;
  margin-top: 12px;
}

.dx-secondary {
  float: right;
  width: 265px;
  padding: 5px 0 !important;
  text-align: right !important;
}

.dx-secondary li {
  min-width: 5em;
}
}
