css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(www.elladodelmal.com)
{
    *           { color: #CFCFCF !important; border-color: #0b0b0b !important; }
    a,
    a *         { color: #2C6DE0 !important; text-decoration: none !important; }

    a:hover,
    a:hover *       { color: #55AA00 !important; }

    a:visited,
    a:visited *     { color: #e31313 !important; }

    a:visited:hover,
    a:visited:hover *   { color: #55AA00 !important; }
    
    *:not(:empty):not([onclick*="open"]):not(SPAN):not([class*="stars"]):not([id*="stars"]):not([id="rating"]):not([class="rating"]):not([class*="SPRITE"]):not([id*="SPRITE"]):not([id*="lbImage"])
    
    { background-image: none !important }

    HTML, BODY { background: none #0a0a0a !important }

    BODY * {background-color: transparent !important}

    DIV[id] {background-color: inherit !important}

    html:root > BODY > * > * > * > *:not(INPUT):not([onclick]) > DIV:not(:empty):not([id])
    
    {background: none #0a0a0a !important}    
}
