css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.wykop.pl") {
    /* miniaturka po prawej */
    .article .media-content{
       float:right !important;
    }
    /* zmniejszona miniaturka */
    .article .media-content img{
       width: 140px !important;
       max-height: 100px !important;
    }
    /* (na głównej) tytuł i opis po lewej */
    .article .lcontrast{
        float:left !important;
        margin-left: 0px !important;
        width:600px;
    }
    /* zmniejszenie czciocionki liczby plusów */
    .vC span{
        font-size:13px !important;
    }
    
    .vC p b {
       min-width:0 !important;
    }
    /* usunięcie ramki wokół + i - */
    .button, button, input.button, input[type="submit"] {
        border:0;
    }
    /* zmniejszenie odstępu między komentarzami i znaleziskami na głównej*/
    .comments-stream > li{
        margin-bottom: 1px !important; 
    }
    
    /* zmniejszenie odstępu między znaleziskami i bocznym paskiem */
    .grid-main {
       margin-right: 317px !important;
    }
    /*zmniejszenie odstępów w bocznym pasku */
    .r-block, .grid-right > .r-block, .grid-right > div {
       margin-bottom: 1px !important;   
    }
    .rbl-block {
       margin-bottom: 1px !important;
    }
    
    body.screening .wrapper {
       width: auto !important;
    }
    
    .diggbox span{
        font-size:14px;
    }
    
    .sub .more a{
        display:block;
        background-color:rgba(0,0,0,0.05)
    }
}
