css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("hootsuite.com") {

.trim {
    background-image: none;
    box-shadow: none;
}
    
#header.trim, #dashboardTabs .tab.active .trim, #streamTabInfo{
    background-color: #eee !important;
}

#dashboardTabs .tab .trim {
    background-color: #ccc !important;
}
    
.globalNav, #dashboardTabs {
    background-color: #333 !important;
    background-image: none;
    box-shadow: none;
}

.stream .header {
    background-color: #19d;
}
    
.stream .header .streamName {
    color: #FFF;
    text-shadow: none;
}
    
.stream .header .streamName .subTitle {
    color: #000;
    text-shadow: none;
}
    
.stream .header .streamName .new {
    text-shadow: none;
    box-shadow: none;
}
    
.message .postAttachment {
    box-shadow: none;
}
    
}
