css:
@-moz-document url("http://macwarrior.fr/index.php?page=chinois") {
    
html{
    background-image: none;
    background-color: #FFF;
}

body{
font-family: monospace;
line-height: 24px;
}

#header {
color: #000000;
}

#content.chinois #data .half, #content.chinois #data .third{
background-color: #EBECEB;
border-radius: 10px;
box-shadow: 5px 5px 5px #888888;
}

#content.chinois #data .half .title, #content.chinois #data .third .title{
color: #000000;
}

#content.chinois #data .half table th{
color: #6B6B6B; 
}

#content.chinois #data .cooldown b{
color: #6B6B6B;
}

#content.chinois #data .cooldown{
color: #7E7F7E;
}

#content.chinois #data .half table tr.color2{
background-color: #FFFFFF;
}

#content.chinois #data .half table tr.color1{
background-color: #F1F1F1;
}

#content.chinois #data .half table.select tbody tr:hover{
background-color: #97D495;
}

#content.chinois #data .half table tr.color3{
background-color: #EBECEB;
}

#content.chinois #data .submit_destinataire{
border: 1px solid #000;
border-radius: 10px;
}

#content.chinois #data .submit_menu {
    background: linear-gradient(to bottom, #9BDB99 0%, #81BD7F 100%) repeat scroll 0 0 rgba(0, 0, 0, 0);
    border-bottom: 3px solid #296427;
    border-top: 1px solid #9BDB99;
    cursor: pointer;
    height: 33px;
    margin-top: 5px;
    width: 150px;
}
#content.chinois #data .submit_menu:hover {
    background: linear-gradient(to bottom, #81BD7F 0%, #9BDB99 100%) repeat scroll 0 0 rgba(0, 0, 0, 0);
}

#content.chinois #data .half table tr.selected, #content.chinois #data .half table tr.selected:hover{
background-color: #5CB559 !important;
}

#footer{
color: #000;
}

}
