css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("dev.mysql.com") {
    div#page {
        font-family: Verdana !important;
    }
    
    div#page div.chapter div.toc {
        margin-bottom: 1em;
    }
    
    dl#tocdetail {
        line-height: 145%;
    }    
    
    div#page div.chapter div.toc dl#tocdetail dd[id^="tocdetail"] {
        margin-top: 0;
    }
    
    div#page p {
        font-family: Verdana !important;
        line-height: 130%;
    }
}
