css:
@-moz-document url-prefix("https://www.duolingo.com/translation/") {
#app.wiki-translation.wide main.main-left {max-width:inherit;min-width:inherit}
#app.wiki-translation .lesson-like-document #document-hider {display:none}
#app.wiki-translation.wide .page-main.main-left {margin:0 auto 2.5em auto !important}
#app.wiki-translation .lesson-like-document .document-header-new {
  height: auto;width:auto;position:static;padding-bottom:1em;
}
#app.wiki-translation .lesson-like-document.view-original .document-header-new {
  height: auto;width:auto;position:static;padding-bottom:1em;
}
#document-instructions {display:none}
#app.wiki-translation .lesson-like-document .responsive-container {
  max-width:inherit;min-width:inherit;padding:0;
}
#app.wiki-translation .lesson-like-document.view-original #original-body-container {width:70%;margin:1em 0 0}
#app .wiki-document-body .wikipedia-article *[class^=infobox] {width:60% !important;margin-left:0.5em}
#app.wiki-translation .lesson-like-document #document-omnibox {width:27%;margin:1em 0 0 1.5em}
#app.wiki-translation .lesson-like-document .wiki-document-body .wiki-paragraph, #app.wiki-translation .lesson-like-document .wiki-document-body p {
  font-size: 1.1em !important;line-height:1.5em;
}
}
