css:
@-moz-document domain("www.nytimes.com") {
  .ad, .ad-placeholder, .marginalia {
    display: none !important;
  }
  .story.theme-main .story-body-text, .story.theme-main .story-addendum-notice, .story.theme-main .story-addendum {
    max-width: 688px !important;
  }
  .type-size-small .story.theme-main .story-body-text {
    font-size: 1.25em !important; /* 20px */
    line-height: 1.5em !important; /* 24px */
  }
  .story.theme-main .story-content {
    margin-left: 144px !important;
  }
  .viewport-large .story.theme-main .story-content {
    width: 100% !important;
  }
  .media.lede, .media.embedded {
    float: none !important;
    margin: 0 auto 45px auto !important;
  }
  .lede-container {
    float: none !important;
  }
}
