css:
@-moz-document domain("jsperf.com") {
  html {
    background-size: 80px;
  }
  html,
  body {
    background-image: url("data:image/png;base64,");
  }
  body > article > h1 {
    text-align: center;
  }
  body > article > form > h3 {
    padding: 0;
    background: none;
    border-bottom: 1px solid white;
  }
  body > article > form > fieldset > div {
    display: flex;
  }
  body > article > form > fieldset > div > label {
    flex: 0 0 auto;
  }
  body > article > form > fieldset > div > input {
    flex: 1 0 auto;
  }
}
