css:
@-moz-document regexp("^https?://www.tumblr.com/dashboard.*") {
.l-header-container.l-header-container--refresh,
#right_column {
  display: none;
}

.post.post_full,
#left_column {
  width: 100%;
}

.post_full .post_media {
  width: 100%!important;
      height: 100%!important;
    }

#posts.posts {
  margin: inherit;
}

#container,
div.post,
.flipcard,
.flipcard .flipcard_front {
  width: inherit;
}

img.post_media_photo,
table.quote_source_table{
  margin: auto;
}


div.post_body
{
  text-align: center;
}

div.post_tags_inner {
  float: inherit;
}

.post_full .post_header,
div.post_header,
div.post:not(.is_photo):not(.is_photoset) div.post_content,
.post .post_content blockquote,
.post_full .post_body p:last-child, .post_full .post_body ol:last-child, .post_full .post_body ul:last-child, .post_full .post_body div:last-child, .post_full .post_body pre:last-child, .post_full .post_body .clear,
.post_full .post_tags,
.post_full .post_body p,
div.post_body>blockquote,
.post_full .post_footer {
  margin: auto;
  text-align: left;
  width: 70%;
  word-wrap: normal;
}

div.post_avatar {
  left: 48%;
  top: -52px;
}

#posts.posts>.post_container {
  margin: 0 0 60px 0;
}

div.photoset {
  margin: auto;
  width: 500px;
}

.post .post_content img {
  max-width: inherit;
}

.post_content_inner {
  background: #eee;
  }

.post_full.is_quote .post_title.large, .post_full.is_quote .post_title.large big, .post_full.is_quote .post_title.large small,
.post_full.is_quote .post_title.medium, .post_full.is_quote .post_title.medium big, .post_full.is_quote .post_title.medium small,
.post_full.is_quote .post_title.small, .post_full.is_quote .post_title.small big, .post_full.is_quote .post_title.small small {
font-size: 4em;
line-height: 1.2em;
font-weight: bold;
font-style: italic;
}

div.post_body {
  font-size: 4em;
}

.l-container {
    width: 100%;
    padding: 0 0 0 0;
  }
}
