css:
@-moz-document domain(instapaper.com) {
.metadata, .helvetica_mode #titlebar, .helvetica_mode #story, .helvetica_mode #footer { font-family: "Verdana" !important}
.story { line-height: 1.35em !important;}
.darkmode .story { color: #CCC}
p {margin: 0 0 1.1em !important}
blockquote {padding: 0px 20px; margin: 10px 0; font-size: 17.5px; border-left: 1px dotted grey}
}
