css:
@-moz-document url-prefix("http://annot.me/"), url-prefix("http://sakuradite.com/") {
/*@import url(http://fonts.googleapis.com/css?family=Source+Sans+Pro:400,700);*/
#background{
background: url('http://fc04.deviantart.net/fs70/i/2012/139/4/a/the_ghost_nebula_by_tylercreatesworlds-d4zb5y3.jpg')no-repeat center center fixed!important;
 -webkit-background-size: cover!important;
  -moz-background-size: cover!important;
  -o-background-size: cover!important;
  background-size: cover!important;
background-color:#0C0613!important;
}
body {
background: url('http://fc04.deviantart.net/fs70/i/2012/139/4/a/the_ghost_nebula_by_tylercreatesworlds-d4zb5y3.jpg')no-repeat center center fixed!important;
 -webkit-background-size: cover!important;
  -moz-background-size: cover!important;
  -o-background-size: cover!important;
  background-size: cover!important;
background-color:#0C0613!important;
}

div.title-content{
  font-family: 'Source Sans Pro', sans-serif!important;
  font-size:2em;
}  

.navbar.nav-sm {opacity: .7!important;}

#forum_topicPost .avatar{border-radius: 30px!important;}
#forum_posts .post .avatar{border-radius: 30px!important;}
.img-rounded {border-radius: 100px!important;}
div#games,.page,.page-fs {
  background-color: rgba(255, 255, 255, 1)!important;
  padding:10px!important;
}
}

@-moz-document url("http://annot.me/game"), url("http://sakuradite.com/game") {
.games.portal {
width: 1100px !important;
  overflow: hidden !important;}
.games.portal table .image.crop{width:180px!important;
  height: 80px!important;}
}

@-moz-document url("http://annot.me/account"), url("http://sakuradite.com/account"), url-prefix("http://annot.me/user/"), url-prefix("http://sakuradite.com/user/") {
.container {width: 1000px!important;}
/*body {line-height: .7em!important; }*/
.col-xs-8{width:auto!important;}
.form-group{margin-bottom:1px!important;}
#account>.left, #user>.left {
    float: left!important;
  	width: 300px !important;
	margin-left: 30px!important;
}
#account>.right, #user>.right {
    float:  left!important;
    width:  300px !important;
    margin-left: 30px!important;
}
#account, #user {
    margin:  100px 20px 20px !important;
    width:  900px !important;
}
.user.portal {
padding: 9px!important;
width: 300px!important;}
.img-rounded {
border-radius: 100px!important;}
.user.portal>.header .avatar {
width: 100px!important;
height: 100px!important;
margin-left: 100px!important;
margin-top: -40px!important;}
.user.portal>.header{padding:0px!important;}
.form-horizontal .control-label {text-align:left!important;}
}
