css:
@-moz-document domain("schoener-fernsehen.com") {
body {
  background-color: #222;
  background-image: none;
}
body > :not(#playerholder):not(#full):not(#tooltip), 
#viewsite #header, 
#bedienung #remoteClick, 
#bedienung #recordsClick, 
#bedienung #tvpilotClick, 
#bedienung #chatClick, 
#bedienung #featureClick {
  visibility: hidden;
  width: 0;
  height: 0;
}
#viewsite, #listsite {
  visibility: visible;
}
#viewsite {
  position: absolute;
  top: 0;
  left: 0;
}
#listsite {
  position: absolute;
  top: 0;
  right: 0;
  bottom: 0;
}
#listsite > * {
  box-shadow: 0px 0px 25px 0px rgba(0,0,0,0.5);
}
#videoplayer, #playerholder {
  padding: 0;
  border: 0;
}
#videoplayer {
  margin-top: 10px;
}
#bedienung img {
  width: 15px;
  height: 15px;
}
#bedienung {
  height: 15px;
  padding-left: 0;
  background-color: rgba(0,0,0,0.5);
}
.odd {
  background-color: #333;
}
.even, 
#programmvorschau, #recordvorschau, #normlists, #settingsheader {
  background-color: #2a2a2a;
}
.epgtr:hover, .epgtr.active, .epgtr:hover .epglistnext, .epgtr.active .epglistnext, .hinttr:hover {
  background-color: #444;
}
}
