css:
/* Version 1.0.8 */

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.yammer.com") {
body { font-size: 14px !important; }


.yj-feed-messages *, #column-one *, #column-three * { font-size: 100% !important; }


#network-header { width: 97% !important }
.page-content { 
    width: 99% !important;
    /*
    margin-top: 10px !important;
    */    
    }
.three-column-layout { background-position: 17% 0 !important }
/* LEFT COLUMN */
   
/* No need to enlarge left column
#column-one { width: 15% !important }
*/
/* Reduce space between groups */
.nav-group-link
    {
    margin: 0 !important;
    }
.nav-group-link .nav-list-link 
    {
    padding: 6px 2px 5px 6px !important;
    }

/* CENTRAL COLUMN */
#column-two {
    padding-left: 10px !important;
    width: 79% !important } /* change this if body is pushed down*/ 

/* Body header */
.filecollab-page-header {
    width: 100% !important;
    }
/* Body text */
.two-column-layout .column-two-left {
    max-width: 800px !important;
    width: 75% !important;
    }
/* Reduced space between items */
.yj-thread-list-item {
    margin-bottom: 10px !important;
    }

.two-column-layout .yj-selector-right-sidebar {
    width: 23% !important;
}
.yj-selector-right-sidebar .content {
    padding: 5px !important;
    margin-bottom: 10px !important;
    }

/* dotted bottom border to distinguish threads */
.yj-thread-list-item--reply-publisher
    {
   	border-style: solid;
	border-bottom: thick dotted navy;
    }

/* 1.0.5 NO SHOUTING PLEASE (Like, Reply, Share buttons) */
    .yj-message-list-item--action-list-item
    ,
    .feed-polish .yj-groupcallout-join-link--link /* JOIN */
    {
    text-transform: capitalize !important;
    }

/* 1.0.6 Colored Date of first post in thread */
.yj-thread-starter .yj-message-time 
    {
    color: navy !important;
    }
/* 1.0.7 Colored Date of last post in thread */
ul .yj-thread-replies li:last-child .yj-message-time {color: red !important;}

/* 1.0.8 Bolded group name in message thread*/
.yj-group-callout--name   {font-weight: bold !important;}    
}
