@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("filmon.com") {

  

  header.primary-header.fixed   {position : relative !important;}


 .share-buttons  ,  .q_picker.right ,   .primary-header {display : none !important;}
 

  body, .base-box {background: #000 !important;}
  
    
  .clearfix, .group-info, .channel-list , .channel ,  .search-result-box   , .chn__top {background: #000 !important;}
  
 
  .group-info .group-channels p { font-size: 13px; color: #e8e8e8!important;}


   .tvg-item.current,   .holder.tvg_car   ,  i  ,  .featured.header.channel_group.i-ellipsis , 

.channel_group.channel-arrow.animate.searchable.i-ellipsis {background: #7c7c7c !important;}
  


     .subscribe-annotation__a , .subscribe-annotation   {display : none !important;}


        .tvguide-listed-header  ,  .tvguide-listed-content.js-tvguide-content  {background: #7c7c7c !important;}


      .button.gray.prev , .button.gray.next    {background: #000 !important;}
   

}
