css:
@-moz-document url-prefix("https://userstyles.org/styles/102645/"), url-prefix("http://userstyles.org/styles/102645/") {
#additional-info-text {
    max-height:none;}
#additional-info-text > a:nth-child(1),#additional-info-text > br:nth-child(2) {
    display: none;}
#additional-info > h2:nth-child(1) {
    display: none;}
#additional-info-text > br:nth-child(9),#additional-info-text > strong:nth-child(10),#additional-info-text > br:nth-child(11) {
    display:none;}
a[href="#main-style-info"]{
  text-decoration: none;  background-color: #e6e6e6;  color: #000;  padding: 2px 4px 2px 4px;  border-top: 1px solid #cecece;  border-right: 1px solid #bababa;  border-bottom: 1px solid #bababa;  border-left: 1px solid #cecece;  border-radius: 3px;  font-size: 14px;}  
a[href="#main-style-info"]:hover{
   color: #000;   border-top: 1px solid #0000ff;   border-right: 1px solid #0000ff;   border-bottom: 1px solid #0000ff;   border-left: 1px solid #0000ff;}    
#below-install-ad > ins:nth-child(3) {
    display: none !important;}
#below-install-ad > .ad-note {
    display:none;}
#below-install-ad {
    margin: 0px 0px 0px 0px;}
}
@-moz-document url-prefix("https://userstyles.org/styles/103158/"), url-prefix("https://userstyles.org/styles/103158/") {
#additional-info > h2:nth-child(1) {
    visibility: hidden;}
#additional-info > h2:after {
  content:'Details';  visibility: visible;  display: block;}
#additional-info-text {
    max-height:none;}
#additional-info-text > ul:nth-child(2) > li:nth-child(2n) {
    list-style-type: none;}  
#below-install-ad > ins:nth-child(3) {
    display: none !important;}
#below-install-ad > .ad-note {
    display:none;}
#below-install-ad {
    margin: 0px 0px 0px 0px;}
}
