css:
@-moz-document domain(buzzfeed.com) {
#buzz_sub_buzz .buzz_superlist_item h2{
    color: #fff;
    clear: both;
    height: 0;
    position: relative;
}
#buzz_sub_buzz h2 span{
    float: left;
    display: block;
    border-radius: 2em;
    padding: .1em .7em;
    background-color: #e32;
    color: #fff;
    text-align: center;
    line-height: 2em;
    font-family: Calibri, Candara, Segoe, "Segoe UI", Optima, Arial, sans-serif;
    font-size: 1em;
    position: absolute;
    z-index: 9999;
    left: -1em;
    top: -.5em;
}
}
