css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("baike.baidu.com") {
/*背景色*/
  html, body,
  .bk-editable-lemma-btns .bk-editable-edit,
  .text .headline-1 .headline-content, .movie-more-content .headline-1 .headline-content,
  #content,
  .editable-title,
  .z-catalog dl,
  .z-catalog-i1 a,
  .z-catalog h2,
  .card-summary,
  .describeTips,
  #search .s_ipt,
  .bacb-more{
    background-color: #F5F5F5;
}
  #content .content-bd 
  {background: none repeat scroll 0% 0% #F5F5F5;}
  .bacb-left-btn, .bacb-right-btn{border: 1px solid #F5F5F5;}  
  
  /*透明背景*/
  #search .s_ipt_wr,
  .text_edit a,
  #lemma-catalog-bottombg,
  .text .headline-1,
  .z-catalog,
  .baseInfoWrap
  {background: none !important;}
  
 /*隐藏元素*/  
#adFlash,  
#adLayerSide,
#encourage_tip_wrapper,  
#userbar .red,  
#bkDynamic,  
#bdshareside,
DIV > UL > A[target="_blank"],
img[src$="logo-baike.png"]
{display:none !important;} 

}
