css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.readmore.de") {
	body {
		background: url('http://i.imgur.com/UV3i2dP.png') no-repeat fixed center top 220px #EEEEEE !important;
	}
    
	#sb-body-inner,
    .search_panel,
    #c_content,
    #gallery_search,
    #vod_search,
    table.group tr td {
		background: #EEEEEE !important;
	}

	#c_left hr, #c_right hr {
		border-bottom: 2px solid #EEEEEE !important;
	}

	#c_right ul.matches.live {
		box-shadow: 0px 2px 0px #EEEEEE, 0px -2px 0px #EEEEEE !important;
	}

	.comment .comment_footer,
    .forum_post .control {
		border-top: 1px solid #EEEEEE !important;
	}

	.pagination ul li.disabled a {
		border-color: #EEEEEE !important;
	}

	#c_left,
    #c_right {
		background: #EAEAEA !important;
	}

	#c_right .headlines_cat span.icon {
		border-left: 1px solid #EAEAEA !important;
	}

	table tr td,
    .comment,
    div.msg.own,
    .forum_post,
    .grid .round .match_container .match,
    table.group,
    table.group tr th {
		background: #E8E8E8 !important;
	}

	#c_right .headlines_cat,
    input[type=submit] {
		background: #E3E3E3 !important;
	}

	input[type=text],
    input[type=password],
    textarea,
    select {
		background: #ECECEC !important;
	}

	#container {
		background: #DEDEDE !important;
	}
}
