css:
@-moz-document domain(prisjakt.nu), domain(pricespy.co.nz), domain(prisjakt.no), domain(pricespy.co.uk) {
div#centered_container {
  width: 100% !important;
}
div#div_produktlista {
  margin-left: 0px !important;
}
div#main_content_wrapper {
  width: 100% !important;
}
ul.list.form {
  table-layout: auto !important;
}
div#headerdiv .super_search_textbox {
  width: calc(100% - 815px);
}
table.list.mixed.pad20-lr.hover-row {
  table-layout: auto !important;
}
div#supersearch_large {
  width: 100% !important;
  background: url("http://i.imgur.com/PuuOJf1.png") #3d7f9b !important;
}
#column1 {
  width: calc(100% - 344px) !important;
}
.front-page #column1 .column2 {
  width: 100% !important;
}
#headerdiv .super_search_textbox_big {
  width: 612px !important;
  margin-left: calc(50% - 529px);
}
#column1:after {
  display: none !important;
}
#supersearch_large .title{
  width: 100% !important;
}
div.feedback {
  width: 100% !important;
}
div#supersearch_large .notice {
  width: 100% !important;
}
#supersearch_large .notice a {
  width: 100% !important;
}
div.minhembio_wrapper {
  width: 100% !important;
}
div.minhembio_wrapper .left {
  width: calc(100% - 319px) !important;
}
#ad_column {
  display: none !important;
}
ul.tabbed_box_tabs {
  white-space: nowrap !important;
}
.control-cell {
  width: 100% !important;
}
#filter_form .checkbuttongroup {
  width: calc(100% - 70px) !important;
}
#search_results .left{
  width: 100% !important;
}
#search_results .right {
  width: 100% !important;
  margin-left: 0px !important;
}
#search_results_forum_topic {
  display: none !important;
}
div.column3 {
  width: 100% !important;
}
div.column2 {
  width: 100% !important;
}
div.span10-5 {
  width: calc(100% - 199px) !important;
}
p#desc_trunc {
  width: 100% !important;
}
div.js-eas-load {
  display: none !important;
}
div#kategori_matris ul {
  width: calc(100% - 20px) !important;
}
#supersearch_large .title, div#supersearch_large .notice, div.feedback {
  width: calc(100% - 23px) !important;
}
}
