css:
@-moz-document domain("tumblr.com") {
.icon_post_text {
visibility: hidden !important;
}

.icon_post_photo {
visibility: hidden !important;
}

.icon_post_quote {
visibility: hidden !important;
}

.icon_post_link {
visibility: hidden !important;
}

.icon_post_chat {
visibility: hidden !important;
}

.icon_post_audio {
visibility: hidden !important;
}

.icon_post_video {
visibility: hidden !important;
}                     
                                           
#dashboard span.tab_notice_value {
color: #000000 !important;
}

#posts .post .permalink {
  opacity: 0 !important;
}

#container .selection_nipple, #container .tab_notice_nipple {
visibility: hidden;
}
                                                           

img[src*="https://secure.assets.tumblr.com/images/post_icons_sprite.png"],
.new_post_label_icon {
    opacity: 0 !important;
}

.new_post_label_text { visibility: hidden;}

#new_post_label_text,
#new_post_label_photo,
#new_post_label_quote,
#new_post_label_link,
#new_post_label_chat,
#new_post_label_audio,
#new_post_label_video {
  background: url("http://i.imgur.com/dlFcDDZ.png") no-repeat !important;
}

#new_post_label_text {
  background-position: 0px 5px !important;
}
#new_post_label_photo {
  background-position: -73px 5px !important;
}
#new_post_label_quote {
  background-position: -140px 5px !important;
}
#new_post_label_link {
  background-position: -220px 5px !important;
}
#new_post_label_chat {
  background-position: -285px 5px !important;
}
#new_post_label_audio {
  background-position: -355px 5px !important;
}
#new_post_label_video {
  background-position: -430px 5px !important;
}

#new_post_label_text:hover {
  background-position: 0px -95px !important;
}
#new_post_label_photo:hover {
  background-position: -73px -95px !important;
}
#new_post_label_quote:hover {
  background-position: -140px -95px !important;
}
#new_post_label_link:hover {
  background-position: -220px -95px !important;
}
#new_post_label_chat:hover {
  background-position: -285px -95px !important;
}
#new_post_label_audio:hover {
  background-position: -355px -95px !important;
}
#new_post_label_video:hover {
  background-position: -430px -95px !important;
}

#posts .post.new_post {
background: transparent url('http://i.imgur.com/7fMMLn2.gif') center bottom !important;
background-position: top center !important;
}
}
