css:
@-moz-document domain(strims.pl) {
#top.border_blue { background: #CEE3F8; }
#top { border-bottom: 1px solid #5F99CF; }
#top_bar { background: #F0F0F0; border-bottom: 1px solid #808080; color:#000; text-transform: uppercase; }
#top_bar_more { background: #F0F0F0; }
#top_bar a:hover { background:#F0F0F0 !important; text-decoration:underline !important; }
#top_bar_wrapper > ul > li { font-size: 10px; }
a.content_title {
color:#0000FF;
}
a.content_title span.bold {
font-weight:normal !important;
font-size: medium;
}
div.content h2.content_header a.content_title:visited {
color:#551A8B;
}
div.content h2.content_header a.content_title:hover {
text-decoration:none;
}
body,* {
font-family:verdana,arial,helvetica,sans-serif;
}
div.template_wrapper { padding: 0 5px; max-width: none; }

#top_bookmarks li a { background: #EFF7FF; border: 0px; color: #336699; font-weight: bold; }
#top_bookmarks li a:hover { text-decoration: underline; }
#top_bookmarks li.selected a { 
background: #fff; 
color:#FF4500; 
border-left: 1px solid #5F99CF; 
border-top: 1px solid #5F99CF; 
border-right: 1px solid #5F99CF; 
}
}
