css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("symfony.com") {
  #sln {
    position: relative;
  }
  
  #sln .sln-bar {
    position: relative;
  }
  
  body {
    padding-top: 0;
  }
  
  header #header-top {
    width: 100%;
  }
  
  header.affix,
  header .affix {
    top: 0;
    box-shadow: 0px 1px 3px rgba(100,100,100,.4);
  }
    
  header.affix ~ .container,
  header .affix ~ .container {
    padding-top: 100px;
  }
  
  header #header-bottom #header-download-sticky {
    background-color: #f9f9f9;
  }
  
  header #header-bottom #header-download-sticky a {
    color: #bcd;
  }
  
  header #header-bottom #header-download-sticky:hover a {
    color: #fff;
  }
}
