css:
@-moz-document domain(gatherer.wizards.com) {

/* Overall - text */
a
{
   color: rgb(255,191,0) !important;
}

a:hover
{
   color: rgb(255,191,0) !important;
}

a:link:hover
{
   color: rgb(255,191,0) !important;
}

a:visited
{
   color: rgb(255,191,0) !important;
}

img
{
   color: rgb(255,191,0) !important;
}

h2
{
   color: rgb(255,191,0) !important;
}

h3
{
   color: rgb(255,191,0) !important;
}

h4
{
   color: rgb(255,191,0) !important;
}

li
{
   color: rgb(255,191,0) !important;
}

p
{
   color: rgb(255,255,255) !important;
}

/* Even sections - Background */
.evenItem
{
   background-color: rgb(46,46,46) !important;
}

/* Comment section - Text */
.discussion .post
{
   color: rgb(255,255,255) !important;
   font-size: 13px !important;
}

/* Comment section - Link to card */
.discussion .post a.autoCard
{
   color: rgb(255,119,0) !important;
}

/* Card Details - 1.2 x zoom to card image */
.cardDetails .cardImage img {
    height: 372px !important;
    width: 267px !important;
}

}
